(()=>{"use strict";class t{condition=document.querySelector(".condition");location=document.querySelector(".location");apiSearch=document.querySelector(".api-search");icon=document.querySelector(".icon");degree=document.querySelector(".degree");feellike=document.querySelector(".feellike");wind=document.querySelector(".wind");humidity=document.querySelector(".humidity");searchLocation="thailand";constructor(){this._fetchData()}async _fetchData(){try{const t=await fetch(`https://api.weatherapi.com/v1/current.json?key=991ba51eacc4429ebf5131813231011&q=${this.searchLocation}`,{mode:"cors"}),e=await t.json();this._insertDetail(e)}catch(t){console.log("Something is wrong ->"+t)}}_insertDetail(t){this.condition.textContent=`${t.current.condition.text}`,this.icon.src=`${t.current.condition.icon}`,this.location.textContent=`${t.location.name} , ${t.location.country}`,this.degree.textContent=`${t.current.temp_c}°C`,this.feellike.textContent=`FEELS LIKE : ${t.current.feelslike_c} °C`,this.wind.textContent=`WIND : ${t.current.wind_mph} MPH`,this.humidity.textContent=`HUMIDITY : ${t.current.humidity}%`}}new class{constructor(){this.weatherApp=new t}}})();