(()=>{"use strict";class e{condition=document.querySelector(".condition");location=document.querySelector(".location");icon=document.querySelector(".icon");degree=document.querySelector(".degree");feellike=document.querySelector(".feellike");wind=document.querySelector(".wind");humidity=document.querySelector(".humidity");searchLocation="thailand";apiSearch=document.querySelector(".api-search");warning=document.querySelector(".warning");constructor(){this._fetchData(),this._searchDate()}async _fetchData(){try{const e=await fetch(`https://api.weatherapi.com/v1/current.json?key=991ba51eacc4429ebf5131813231011&q=${this.searchLocation}`,{mode:"cors"}),t=await e.json();this.warning.classList.add("hide"),this._insertHTML(t)}catch(e){this.warning.classList.remove("hide"),console.log("Error fetching data:"+e)}}_insertHTML(e){this.condition.textContent=`${e.current.condition.text}`,this.icon.src=`${e.current.condition.icon}`,this.location.textContent=`${e.location.name} , ${e.location.country}`,this.degree.textContent=`${e.current.temp_c}°C`,this.feellike.textContent=`FEELS LIKE : ${e.current.feelslike_c} °C`,this.wind.textContent=`WIND : ${e.current.wind_mph} MPH`,this.humidity.textContent=`HUMIDITY : ${e.current.humidity}%`}_searchDate(){this.apiSearch.addEventListener("keypress",(e=>{"Enter"===e.key&&(this.searchLocation=this.apiSearch.value,this._fetchData(),this.apiSearch.value="")}))}}new class{constructor(){this.weatherApp=new e}}})();